int main ( ) { int A , B , T , i , max , min , lcm , gcd , j ; scanf ( "%d" , & T ) ; for ( i = 0 ; i < T ; i ++ ) { scanf ( "%d%d" , & A , & B ) ; max = ( A > B ) ? A : B ; min = ( A > B ) ? B : A ; while ( 1 ) { if ( max % A == 0 && max % B == 0 ) { lcm = max ; break ; } ++ max ; } for ( j = min ; j >= 1 ; -- j ) { if ( A % j == 0 && B % j == 0 ) { gcd = j ; break ; } } printf ( "%d\t%d\n" , gcd , lcm ) ; } return 0 ; }