int max ( int a , int b ) { if ( a > b ) return a ; else return b ; } int main ( ) { int i = 0 , j = 0 ; int n = 0 ; int rows = 0 ; int num = 0 ; int k = 0 ; int maxSum = 0 ; int matrix [ 100 ] [ 100 ] ; for ( j = 0 ; j < 99 ; j ++ ) { matrix [ j ] [ j + 1 ] = 0 ; } scanf ( "%d" , & n ) ; for ( i = 0 ; i < n ; i ++ ) { maxSum = 0 ; scanf ( "%d" , & rows ) ; if ( rows != 0 ) { scanf ( "%d" , & num ) ; maxSum = num ; matrix [ 0 ] [ 0 ] = num ; } for ( j = 1 ; j < rows ; j ++ ) { scanf ( "%d" , & num ) ; matrix [ j ] [ 0 ] = matrix [ j - 1 ] [ 0 ] + num ; maxSum = matrix [ j ] [ 0 ] > maxSum ? matrix [ j ] [ 0 ] : maxSum ; for ( k = 1 ; k <= j ; k ++ ) { scanf ( "%d" , & num ) ; matrix [ j ] [ k ] = max ( matrix [ j - 1 ] [ k ] + num , matrix [ j - 1 ] [ k - 1 ] + num ) ; maxSum = matrix [ j ] [ k ] > maxSum ? matrix [ j ] [ k ] : maxSum ; } } printf ( "%d\n" , maxSum ) ; } return 0 ; }