main ( ) { int t , i , p , first , j , k , max ; int sum = 0 ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { sum = 0 ; scanf ( "%d" , & p ) ; for ( j = 1 ; j <= p ; j ++ ) { int n [ j ] ; for ( k = 0 ; k < j ; k ++ ) { scanf ( "%d" , & n [ k ] ) ; } max = n [ 0 ] ; for ( k = 1 ; k < j ; k ++ ) { if ( max < n [ k ] ) max = n [ k ] ; } sum = sum + max ; } printf ( "%d\n" , sum ) ; } }