void main ( ) { int i , tc , a , b , l , h , low , high , r , j , k ; scanf ( "%d" , & tc ) ; for ( j = 0 ; j < tc ; j ++ ) { scanf ( "%d %d" , & a , & b ) ; if ( a > b ) { l = a ; low = a ; h = b ; high = b ; } else { l = b ; h = a ; low = b ; high = a ; } k = high ; while ( 1 ) { r = h % l ; if ( r == 0 ) { printf ( "%d " , l ) ; break ; } h = l ; l = r ; } while ( 1 ) { if ( k % low == 0 && k % high == 0 ) { printf ( "%d\n" , k ) ; break ; } k = k + high ; } } }