long int gcd ( long int m , long int n ) { long int rem , a , b ; if ( m > n ) { a = m ; b = n ; } else { a = n ; b = m ; } if ( ( a >= 0 ) && ( b >= 0 ) ) { while ( a % b != 0 ) { rem = a % b ; a = b ; b = rem ; } } return ( b ) ; } int main ( ) { long int m , n , res , i , t , lcm ; scanf ( "%ld" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { printf ( "\n enter two nos. " ) ; scanf ( "%ld %ld" , & m , & n ) ; if ( n > 0 ) { res = gcd ( m , n ) ; lcm = ( m * n ) / res ; printf ( "%ld %ld \n" , res , lcm ) ; } else printf ( "\n invalid value" ) ; } return 0 ; }