int main ( ) { int t ; long a [ 50000 ] , n , i , min ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%ld" , & n ) ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%ld" , & a [ i ] ) ; if ( i == 0 ) min = a [ 0 ] ; else { if ( a [ i ] < min ) min = a [ i ] ; } } if ( n >= 2 && n <= 15 ) { i = min * ( n - 1 ) ; printf ( "%ld\n" , i ) ; } else if ( n > 15 && n <= 100 ) { i = min * ( n - 1 ) ; printf ( "%ld\n" , i ) ; } else if ( n > 100 && n <= 50000 ) { i = min * ( n - 1 ) ; printf ( "%ld\n" , i ) ; } } return 0 ; }