int main ( ) { int T ; scanf ( "%d" , & T ) ; int i ; for ( i = 0 ; i < T ; i ++ ) { int n ; scanf ( "%d" , & n ) ; long long int arr [ n ] ; int j ; for ( j = 0 ; j < n ; j ++ ) { scanf ( "%lld" , & arr [ j ] ) ; } int len = 1 ; int sum = 0 ; for ( j = 0 ; j < n - 1 ; j ++ ) { if ( arr [ j + 1 ] >= arr [ j ] ) { len ++ ; } else { sum += ( ( len * ( len - 1 ) ) / 2 ) ; len = 1 ; } } if ( len > 1 ) { sum += ( ( len * ( len - 1 ) ) / 2 ) ; } printf ( "%d\n" , sum + n ) ; } return 0 ; }