int hcf ( int b , int c ) ; int lcm ( int b , int c ) ; int main ( ) { int a , i , b , c , d , e ; scanf ( "%d" , & a ) ; for ( i = 0 ; i < a ; i ++ ) { scanf ( "%d %d" , & b , & c ) ; d = hcf ( b , c ) ; e = lcm ( b , c ) ; printf ( "%d %d" , d , e ) ; } } int hcf ( int b , int c ) { int min , j ; if ( b < c ) min = b ; else min = c ; if ( b % min == 0 && c % min == 0 ) return min ; for ( j = min / 2 ; j >= 1 ; j -- ) { if ( b % j == 0 && c % j == 0 ) return j ; } return 0 ; } int lcm ( int b , int c ) { int max , j , lcm = 1 ; if ( b > c ) max = b ; else max = c ; for ( j = 2 ; j <= max ; j ++ ) { while ( b % j == 0 || c % j == 0 ) { if ( b % j == 0 ) b = b / j ; if ( c % j == 0 ) c = c / j ; lcm = lcm * j ; } } return lcm ; }