int main ( ) { int t , n , i = 0 , k , a [ 2 ] , count = 0 ; long long int sum = 0 ; scanf ( "%d" , & t ) ; while ( t -- ) { sum = 0 ; scanf ( "%d" , & n ) ; for ( count = 0 , i = 0 ; count < n ; i ++ , count ++ ) { scanf ( "%d" , & a [ i ] ) ; if ( i == 1 ) { if ( a [ i - 1 ] < a [ i ] ) { k = a [ i - 1 ] ; sum = sum + a [ i - 1 ] ; } else { k = a [ i ] ; sum = sum + a [ i ] ; } i = 0 ; a [ i ] = k ; } } count = 0 ; printf ( "%d\n" , sum ) ; } return 0 ; }