void main ( ) { int t , i , j , c , subnum , alen ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%d" , & alen ) ; int arr [ alen ] ; c = 1 ; subnum = 1 ; scanf ( "%d" , & arr [ 0 ] ) ; for ( j = 1 ; j < alen ; j ++ ) { scanf ( "%d" , & arr [ j ] ) ; if ( arr [ j ] >= arr [ j - 1 ] ) { c ++ ; subnum += c ; } else { c = 1 ; subnum ++ ; } } printf ( "%d\n" , subnum ) ; } }