int main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { long long unsigned int a , b , temp , l ; scanf ( "%llu %llu" , & a , & b ) ; double lcm = a * b ; if ( a < b ) { a = a + b ; b = a - b ; a = a - b ; } while ( a % b != 0 ) { temp = b ; b = a % b ; a = temp ; } lcm = lcm / b ; printf ( "%llu %llu\n" , b , ( long long unsigned int ) lcm ) ; } return 0 ; }