int main ( void ) { int curr [ 100 ] , prev [ 100 ] ; int input , i , j , t , n , max ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d" , & n ) ; scanf ( "%d" , & prev [ 0 ] ) ; for ( i = 1 ; i < n ; i ++ ) { for ( j = 0 ; j <= i ; j ++ ) { scanf ( "%d" , & input ) ; if ( j == 0 ) curr [ j ] = input + prev [ j ] ; else if ( j == i ) curr [ j ] = input + prev [ j - 1 ] ; else if ( prev [ j ] > prev [ j - 1 ] ) curr [ j ] = input + prev [ j ] ; else curr [ j ] = input + prev [ j - 1 ] ; } for ( j = 0 ; j <= i ; j ++ ) prev [ j ] = curr [ j ] ; } for ( j = 0 , max = 0 ; j < n ; j ++ ) { if ( curr [ max ] < curr [ j ] ) max = j ; } printf ( "%d\n" , curr [ max ] ) ; } return 0 ; }