long int gcd ( long int a , long int b ) { if ( b != 0 ) return gcd ( b , a % b ) ; else return a ; } long long int lcm ( int a , int b ) { return ( ( a * b ) / gcd ( a , b ) ) ; } int main ( ) { int n , i ; long long int a [ 1000 ] , b [ 1000 ] ; scanf ( "%d" , & n ) ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%lld%lld" , & a [ i ] , & b [ i ] ) ; } for ( i = 0 ; i < n ; i ++ ) { printf ( "%ld %lld\n" , gcd ( a [ i ] , b [ i ] ) , lcm ( a [ i ] , b [ i ] ) ) ; } return 0 ; }