int gcd ( int a , int b ) { return b == 0 ? a : gcd ( b , a % b ) ; } int lcm ( int a , int b ) { return ( a * b ) / gcd ( a , b ) ; } int main ( void ) { int t ; scanf ( "%d" , & t ) ; for ( ; t > 0 ; -- t ) { int a , b ; scanf ( "%d %d" , & a , & b ) ; printf ( "%d " , gcd ( a , b ) ) ; printf ( "%d\n" , lcm ( a , b ) ) ; } }