int main ( ) { int a [ 100 ] , n , s [ 100 ] , i , j , t ; scanf ( "%d" , & t ) ; while ( t > 0 ) { s [ 0 ] = 0 ; scanf ( "%d" , & n ) ; for ( i = 1 ; i <= n ; i ++ ) { for ( j = 1 ; j <= i ; j ++ ) scanf ( "%d" , & a [ j ] ) ; s [ i ] = s [ i - 1 ] + a [ i ] ; for ( j = i - 1 ; j > 0 ; j -- ) { if ( s [ j - 1 ] > s [ j ] ) s [ j ] = s [ j - 1 ] + a [ j ] ; else s [ j ] += a [ j ] ; } } for ( j = 1 , n = 0 ; j <= i ; j ++ ) if ( s [ j ] > n ) n = s [ j ] ; printf ( "%d\n" , n ) ; t -- ; } return 0 ; }