int gcd ( ) ; void lcm ( ) ; int main ( ) { int a , b , r , T ; scanf ( "%d" , & T ) ; while ( T > 0 ) { scanf ( "%d %d" , & a , & b ) ; r = gcd ( a , b ) ; printf ( "%d " , r ) ; lcm ( a , b ) ; T -- ; } return 0 ; } int gcd ( x , y ) { if ( x >= y ) { if ( x % y == 0 ) return y ; else return ( gcd ( x - y , y ) ) ; } else { if ( x % y == 0 ) return y ; else return ( gcd ( x , y - x ) ) ; } } void lcm ( a , b ) { if ( a >= b ) { if ( a % b == 0 ) printf ( "%d\n" , a ) ; else printf ( "%d\n" , ( a * b ) ) ; } else { if ( b % a == 0 ) { printf ( "%d\n" , b ) ; } else printf ( "%d\n" , ( a * b ) ) ; } }