int main ( ) { int n , max , i , j , tc , c , rows , k , p ; int sum [ 10 ] ; int a [ 10 ] [ 10 ] ; max = sum [ 0 ] ; sum [ 0 ] = 0 ; scanf ( "%d" , & n ) ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%d" , & tc ) ; for ( j = 0 ; j < tc ; j ++ ) { scanf ( "%d" , & rows ) ; for ( k = 0 ; k < rows ; k ++ ) { for ( c = 0 ; c < rows ; c ++ ) { sum [ k ] = sum [ k ] + a [ k ] [ c ] ; } for ( p = 0 ; p < rows ; p ++ ) { if ( max < sum [ p ] ) max = sum [ p ] ; } } } printf ( "%d\n" , max ) ; } return 0 ; }