int main ( void ) { int T , i , j , k ; long int A , B ; scanf ( "%d" , & T ) ; if ( T >= 1 && T <= 1000 ) { for ( i = 0 ; i < T ; i ++ ) { scanf ( "%ld %ld" , & A , & B ) ; if ( ( A >= 1 && A <= 1000000 ) && ( B >= 1 && B <= 1000000 ) ) { if ( A > B ) { k = 1 ; for ( j = A ; j >= 1 ; j -- ) { if ( A % j == 0 && B % j == 0 ) break ; } printf ( "%d " , j ) ; while ( ( A * k ) % B ) k ++ ; printf ( "%ld" , A * k ) ; } } printf ( "\n" ) ; } } return ( 0 ) ; }