int gcd ( int , int ) ; int lcm ( int , int ) ; void main ( ) { int a , b , n , i , g , l ; scanf ( "%d" , & n ) ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%d %d" , & a , & b ) ; g = gcd ( a , b ) ; l = lcm ( a , b ) ; printf ( "%d %d" , g , l ) ; } } int gcd ( int a , int b ) { if ( a < b ) gcd ( a , b - a ) ; else if ( a > b ) gcd ( a - b , b ) ; else return a ; } int lcm ( int a , int b ) { return ( a * b ) / ( gcd ( a , b ) ) ; }