int gcd ( long int a , long int b ) ; int main ( ) { long int t , a , b , hcf , lcm ; scanf ( "%ld\n" , & t ) ; while ( t -- ) { scanf ( "%ld %ld" , & a , & b ) ; hcf = gcd ( a , b ) ; lcm = ( a * b ) / hcf ; printf ( "%ld %ld\n" , hcf , lcm ) ; } return 0 ; } int gcd ( long int a , long int b ) { int dividend = a >= b ? a : b ; int divisor = a <= b ? a : b ; while ( divisor != 0 ) { int remainder = dividend % divisor ; dividend = divisor ; divisor = remainder ; } return dividend ; }