int main ( ) { int t , k ; scanf ( "%d" , & t ) ; int s [ t ] ; for ( k = 1 ; k <= t ; k ++ ) { int n ; scanf ( "%d" , & n ) ; int a [ n ] [ n ] , i , j ; for ( i = 0 ; i < n ; i ++ ) for ( j = 0 ; j < n ; j ++ ) { a [ i ] [ j ] = 0 ; } for ( i = 0 ; i < n ; i ++ ) for ( j = 0 ; j < i + 1 ; j ++ ) { scanf ( "%d" , & a [ i ] [ j ] ) ; } int sum = a [ 0 ] [ 0 ] , sumbottom = 0 , sumright = 0 , val = 0 ; for ( i = 0 ; i < ( n - 1 ) ; i ++ ) { sumbottom = 0 ; sumright = 0 ; for ( j = i + 1 ; j < n ; j ++ ) { sumbottom += a [ j ] [ val ] ; ( val <= j ) ? ( sumright += a [ j ] [ val + 1 ] ) : ( sumright = 0 ) ; } printf ( "%d %d\n" , sumbottom , sumright ) ; if ( sumright > sumbottom ) { sum += a [ i + 1 ] [ val + 1 ] ; val += 1 ; } else sum += a [ i + 1 ] [ val ] ; } s [ k ] = sum ; } for ( k = 1 ; k <= t ; k ++ ) { printf ( "%d\n" , s [ k ] ) ; } return 0 ; }