int main ( ) { int input [ 100 ] [ 100 ] , t ; scanf ( "%d" , & t ) ; while ( t -- ) { int i = 0 , j = 0 , n , count = 0 , x , a = 0 , b = 0 ; scanf ( "%d" , & n ) ; for ( i = 0 ; i <= n ; i ++ ) { for ( j = 0 ; j <= i ; j ++ ) { scanf ( "%d" , & input [ i ] [ j ] ) ; } } x = input [ 0 ] [ 0 ] ; do { if ( input [ a + 1 ] [ b ] > input [ a + 1 ] [ b + 1 ] ) { x = x + input [ a + 1 ] [ b ] ; a = a + 1 ; count ++ ; } else { x = x + input [ a + 1 ] [ b + 1 ] ; a = a + 1 ; b = b + 1 ; count ++ ; } } while ( count != n ) ; printf ( "%d\n" , x ) ; } return 0 ; }