int main ( ) { int p , i , j ; long int n ; scanf ( "%d" , & p ) ; while ( p -- ) { scanf ( "%ld" , & n ) ; int a [ n ] ; for ( i = 0 ; i < n ; i ++ ) scanf ( "%d" , & a [ i ] ) ; for ( i = 0 ; i < n - 1 ; i ++ ) { for ( j = 0 ; j < n - 1 - i ; j ++ ) { if ( a [ j ] >= a [ j + 1 ] ) { int t = a [ j ] ; a [ j ] = a [ j + 1 ] ; a [ j + 1 ] = t ; } } } printf ( "%d" , a [ 0 ] * ( n - 1 ) ) ; } return 0 ; }