int main ( ) { long long int num1 , num2 , gcd , lcm , rem , a , b , t ; scanf ( "%lld" , & t ) ; while ( t -- ) { scanf ( "%lld %lld" , & num1 , & num2 ) ; if ( num1 > num2 ) { a = num1 ; b = num2 ; } else { a = num2 ; b = num1 ; } rem = a % b ; while ( rem != 0 ) { a = b ; b = rem ; rem = a % b ; } gcd = b ; lcm = num1 * num2 / gcd ; printf ( "%lld %lld\n" , gcd , lcm ) ; } return 0 ; }