int main ( ) { int a ; scanf ( "%d" , & a ) ; while ( a -- ) { long long unsigned int p , k = 0 , sum = 0 ; scanf ( "%llu" , & p ) ; long long unsigned int s [ p ] ; for ( int i = 0 ; i < p ; i ++ ) { scanf ( "%llu" , & s [ i ] ) ; } for ( int i = 0 ; i < p - 1 ; i ++ ) { if ( s [ i ] <= s [ i + 1 ] ) { k ++ ; continue ; } if ( k > 0 ) { for ( int i = 1 ; i <= k + 1 ; i ++ ) { sum += ( k + 1 - i ) ; k = 0 ; } } } for ( int i = 1 ; i <= k + 1 ; i ++ ) { sum += ( k + 1 - i ) ; } sum += p ; printf ( "%llu\n" , sum ) ; } return 0 ; }