int gcd ( int a , int b ) { if ( a == 0 || b == 0 ) { return 0 ; } if ( a == b ) { return a ; } if ( a > b ) { return gcd ( ( a - b ) , b ) ; } return gcd ( a , ( b - a ) ) ; } int main ( void ) { int t ; long int g , a , b ; scanf ( "%d" , & t ) ; for ( int i = 0 ; i < t ; i ++ ) { scanf ( "%ld%ld" , & a , & b ) ; g = gcd ( a , b ) ; printf ( "%ld\t" , g ) ; printf ( "%ld\n" , ( a * b ) / g ) ; } return 0 ; }