int main ( void ) { int t , i ; int n ; long long int ans ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d" , & n ) ; int a [ n ] ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%d" , & a [ i ] ) ; } int sml = a [ 0 ] ; for ( i = 1 ; i < n ; i ++ ) { if ( a [ i ] < sml ) sml = a [ i ] ; } if ( n == 1 ) printf ( "%d\n" , a [ 0 ] ) ; else { ans = ( long long ) sml * ( long long ) ( n - 1 ) ; printf ( "%lld\n" , ans ) ; } } return 0 ; }