void main ( ) { int a [ 1000 ] , t , n , count , p , i , j ; printf ( "enter the no. of cases" ) ; scanf ( "%d" , & t ) ; for ( p = 0 ; p < t ; p ++ ) { count = 0 ; printf ( "\n enter the size of array %d" , p + 1 ) ; scanf ( "%d" , & n ) ; printf ( "\n enter the array row wise" ) ; for ( i = 0 ; i < n ; i ++ ) scanf ( "%d" , & a [ i ] ) ; for ( i = 0 ; i < n ; i ++ ) { for ( j = 0 ; j < n ; j ++ ) { if ( a [ i ] < a [ j ] ) count += 1 ; } } printf ( "\n no. of subarrays in this case are %d" , count ) ; } }