int gcd ( int a , int b ) { if ( b == 0 ) return a ; else return gcd ( b , a % b ) ; } int LCM ( int a , int b ) { return ( a * b ) / gcd ( a , b ) ; } int main ( ) { int T ; long long int A , B ; int lcm , hcf ; scanf ( "%d" , & T ) ; while ( T -- ) { scanf ( "%lli %lli" , & A , & B ) ; hcf = gcd ( A , B ) ; lcm = LCM ( A , B ) ; printf ( "%d %d \n" , hcf , lcm ) ; } return 0 ; }