int subinc ( ) { int n ; scanf ( "%d" , & n ) ; long long int arr [ n ] ; int i = 0 , j = 0 , count = n , flag = 0 ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%d" , & arr [ i ] ) ; } i = 0 ; for ( i = 0 ; i < n - 1 ; i ++ ) { j = 0 ; flag = 0 ; while ( arr [ i ] <= arr [ i + j ] && flag == 0 ) { if ( j > 0 ) { count ++ ; flag = 1 ; } j ++ ; } } return count ; } int main ( ) { int t ; scanf ( "%d" , & t ) ; int i , count ; int arr [ t ] ; for ( i = 1 ; i <= t ; i ++ ) { count = subinc ( ) ; arr [ i - 1 ] = count ; } for ( i = 0 ; i < t ; i ++ ) { printf ( "%d\n" , arr [ i ] ) ; } return 0 ; }