int main ( ) { int test , i , num1 , num2 , temp , flag , a , lcm ; scanf ( "%d\n" , & test ) ; for ( i = 1 ; i <= test ; i ++ ) { temp = 0 ; flag = 0 ; scanf ( "%d%d" , & num1 , & num2 ) ; if ( num1 < num2 ) { temp = num2 ; num2 = num1 ; num1 = temp ; } a = num1 * num2 ; while ( num1 % num2 != 0 ) { flag = num2 ; num2 = num1 % num2 ; num1 = flag ; } lcm = a / num2 ; printf ( "%d %d\n" , num2 , lcm ) ; } }