int main ( ) { long long int t , i , p , n , m , max , l , k ; scanf ( "%lld" , & t ) ; k = 0 ; while ( k < t ) { k ++ ; scanf ( "%lld %lld" , & n , & m ) ; p = n * m ; if ( n > m ) max = n ; else max = m ; for ( i = 1 ; i < max ; i ++ ) { if ( n % i == 0 && m % i == 0 ) max = i ; } l = p / max ; printf ( "%lld %lld \n" , max , l ) ; } return 0 ; }