int main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { int A , B , GCD , LCM , i , x , y , L , M ; scanf ( "%d %d" , & A , & B ) ; if ( A >= B ) { for ( i = A ; i <= A * B ; i ++ ) { x = i % A ; y = i % B ; if ( ( x == 0 ) && ( y == 0 ) ) { LCM = i ; break ; } } } else { for ( i = B ; i <= A * B ; i ++ ) { L = i % A ; M = i % B ; if ( ( L == 0 ) && ( M == 0 ) ) { LCM = i ; break ; } } } GCD = ( A * B ) / LCM ; printf ( "%d %d\n" , GCD , LCM ) ; } return 0 ; }