main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { int a , b , v , u , i , lcm ; scanf ( "%d%d" , & a , & b ) ; if ( a > b ) { u = b ; } else { u = a ; } int aa = a , bb = b ; v = 1 ; i = 2 ; while ( u >= i ) { if ( a % i == 0 && b % i == 0 ) { a = a / i ; b = b / i ; v = v * i ; i -- ; } if ( i > a || i > b ) break ; i ++ ; } printf ( "%d\n" , v ) ; lcm = ( ( aa * bb ) / v ) ; printf ( "%d\n" , lcm ) ; } }