int main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { int a , b , num , den , rem , gcd , lcm ; scanf ( "%d %d" , & a , & b ) ; if ( a > b ) { num = a ; den = b ; } else { num = b ; den = a ; } rem = num % den ; while ( rem != 0 ) { num = den ; den = rem ; rem = num % den ; } gcd = den ; lcm = a * b / gcd ; printf ( "%d\t" , gcd ) ; printf ( "%d\n" , lcm ) ; } return 0 ; }