int max ( int a , int b ) { int max = a ; if ( max < b ) { max = b ; } return max ; } int GCD ( int a , int b ) { if ( b == 0 ) return a ; int m = max ( a , b ) ; int n = a + b - max ( a , b ) ; int x = m % n ; m = n ; n = x ; GCD ( m , n ) ; } int main ( void ) { int t ; scanf ( "%d" , & t ) ; int i ; for ( i = 0 ; i < t ; i ++ ) { int a , b ; scanf ( "%d %d" , & a , & b ) ; printf ( "%d %d\n" , GCD ( a , b ) , a * b / ( GCD ( a , b ) ) ) ; } return 0 ; }