int main ( ) { int n , i , j ; int lines ; int max , max1 = 0 ; int arr [ 100 ] [ 100 ] ; int res [ 1000 ] ; int cnt = 0 ; scanf ( "%d" , & n ) ; while ( n > 0 ) { scanf ( "%d" , & lines ) ; max = 0 ; for ( i = 0 ; i < lines ; i ++ ) { for ( j = 0 ; j <= i ; j ++ ) { scanf ( "%d" , & arr [ i ] [ j ] ) ; max1 = 0 ; if ( i - 1 >= 0 ) { max1 = arr [ i - 1 ] [ j ] ; if ( j - 1 >= 0 ) { if ( arr [ i - 1 ] [ j - 1 ] > arr [ i - 1 ] [ j ] ) { max1 = arr [ i - 1 ] [ j - 1 ] ; } } } arr [ i ] [ j ] += max1 ; if ( i == lines - 1 ) { if ( arr [ i ] [ j ] > max ) { max = arr [ i ] [ j ] ; } } } } n -- ; res [ cnt ++ ] = max ; } for ( i = 0 ; i < cnt ; i ++ ) { printf ( "%d\n" , res [ i ] ) ; } return 0 ; }