int main ( ) { long long int A , B , gcd , j , lcm , a , b , rem , n ; int T , i ; scanf ( "%d" , & T ) ; printf ( "\n" ) ; if ( T >= 1 && T <= 1000 ) { for ( i = 1 ; i <= T ; i ++ ) { scanf ( "%lld %lld" , & A , & B ) ; if ( A >= 1 && A <= 1000000 && B >= 1 && B <= 1000000 ) { a = A ; b = B ; if ( a < b ) { a = a + b ; b = a - b ; a = a - b ; } for ( ; ( a % b ) != 0 ; ) { rem = a % b ; a = b ; b = rem ; } gcd = b ; a = A ; b = B ; lcm = 1 ; n = b ; if ( a < b ) { a = a + b ; b = a - b ; a = a - b ; } for ( j = 2 ; j <= pow ( n , 1 / 2 ) ; ) { if ( a % j == 0 && b % j == 0 ) { a = a / j ; b = b / j ; lcm = lcm * j ; } else { if ( ( j + 1 ) % 2 == 0 ) j += 2 ; else j ++ ; } } lcm = lcm * a * b ; printf ( "\n%lld %lld" , gcd , lcm ) ; } } } return 0 ; }