long int gcd ( long int a , long int b ) { if ( a == 0 || b == 0 ) return 0 ; if ( a == b ) return a ; if ( a > b ) return gcd ( a - b , b ) ; return gcd ( a , b - a ) ; } long int lcm ( long int a , long int b ) { return ( a * b ) / gcd ( a , b ) ; } int main ( ) { long int a , b ; int n , i ; scanf ( "%d" , & n ) ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%lld %lld" , & a , & b ) ; printf ( "%lld %lld\n" , gcd ( a , b ) , lcm ( a , b ) ) ; } }