long gcd ( long a , long b ) { while ( 1 ) { if ( a >= b ) { if ( a % b == 0 ) return b ; else a = a - b ; } else { if ( b % a == 0 ) return a ; else b = b - a ; } } } int main ( ) { long t ; long a , b , d ; long c ; scanf ( "%d" , & t ) ; for ( int i = 0 ; i < t ; i ++ ) { scanf ( "%d" , & a ) ; scanf ( "%d" , & b ) ; d = gcd ( a , b ) ; c = ( a * b ) / d ; printf ( "%d " , d ) ; printf ( "%d\n" , c ) ; } return 0 ; }