int main ( ) { int t , n ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d" , & n ) ; int a [ n ] , i , min , cost = 0 ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%d" , & a [ i ] ) ; } while ( n > 1 ) { for ( i = n - 1 ; i >= 0 ; i -- ) { if ( a [ i ] < a [ i - 1 ] ) { cost = cost + a [ i ] ; a [ i - 1 ] = a [ i ] ; n -- ; } else { cost = cost + a [ i - 1 ] ; n -- ; } } } printf ( "%d\n" , cost ) ; } return 0 ; }