int main ( ) { int t , n , i , j , a [ n ] [ n ] , sum ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d" , & n ) ; sum = 0 ; for ( i = 1 ; i <= n ; i ++ ) { for ( j = 1 ; j <= i ; j ++ ) { scanf ( "%d" , & a [ i ] [ j ] ) ; } } for ( i = 1 ; i <= n ; i ++ ) { for ( j = 1 ; j <= i ; j ++ ) { if ( j == n && a [ j ] [ 1 ] < a [ j ] [ 2 ] ) sum += a [ i ] [ 2 ] ; else sum += a [ i ] [ 1 ] ; } } printf ( "%d\n" , sum ) ; } return 0 ; }