int main ( ) { int t , a , b , hcf , j , i , ctr = 0 ; scanf ( "%d" , & t ) ; while ( t > 0 ) { scanf ( "%d" , & a ) ; scanf ( "%d" , & b ) ; for ( j = 1 ; ; j ++ ) { if ( a % j == 0 && b % j == 0 ) { hcf = j ; break ; } } printf ( "%d" , hcf ) ; for ( i = 2 ; i <= ( a * b ) ; i ++ ) { if ( i % a == 0 && i % b == 0 && ctr == 0 ) { printf ( " %d\n" , i ) ; ctr ++ ; } } t -- ; ctr = 0 ; hcf = 0 ; a = 0 ; b = 0 ; } }