long int max ( long int a , long int b ) { if ( a > b ) { return a ; } else { return b ; } } long int gcd ( long int c , long int d ) { long int temp ; while ( c % d ) { temp = d ; d = c % d ; c = temp ; } return d ; } long int lcm ( long int c , long int d ) { return c * d ; } int main ( void ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { long int a , b ; scanf ( "%d" , & a ) ; scanf ( "%d" , & b ) ; long int c = max ( a , b ) ; long int d = a + b - c ; printf ( "%ld" , gcd ( c , d ) ) ; printf ( " %ld\n" , ( a * b ) / gcd ( c , d ) ) ; } return 0 ; }