int main ( ) { int no_test_cases , nos_in_line [ 101 ] , i , j , no_of_row = 0 , sel_j = 0 , sel_var1 = 0 ; int max_sum = 0 ; scanf ( "%d" , & no_test_cases ) ; while ( no_test_cases -- ) { scanf ( "%d" , & no_of_row ) ; for ( i = 1 ; i <= no_of_row ; i ++ ) { for ( j = 0 ; j < i ; j ++ ) { scanf ( "%d" , & nos_in_line [ j ] ) ; if ( j == sel_j ) { sel_var1 = nos_in_line [ j ] ; } else if ( ( j == ( sel_j + 1 ) ) && ( sel_var1 < nos_in_line [ j ] ) ) { sel_var1 = nos_in_line [ j ] ; sel_j = j ; } } max_sum = max_sum + sel_var1 ; } printf ( "%d\n" , max_sum ) ; } return 0 ; }