int comp ( int * * x , int i , int j , int a ) { int suml , sumr , summax ; if ( i == a - 1 ) return x [ i ] [ j ] ; else { suml = comp ( x , i + 1 , j , a ) ; sumr = comp ( x , i + 1 , j + 1 , a ) ; summax = suml ; if ( sumr > summax ) summax = sumr ; return ( summax + x [ i ] [ j ] ) ; } } int main ( ) { int n , i , a , j , sum ; int * * x ; scanf ( "%d" , & n ) ; while ( n -- ) { sum = 0 ; scanf ( "%d" , & a ) ; x = ( int * * ) malloc ( sizeof ( int * ) * a ) ; for ( i = 0 ; i < a ; i ++ ) { x [ i ] = ( int * ) malloc ( sizeof ( int ) * ( i + 1 ) ) ; for ( j = 0 ; j < ( i + 1 ) ; j ++ ) { scanf ( "%d" , & x [ i ] [ j ] ) ; } } sum = comp ( x , 0 , 0 , a ) ; printf ( "%d\n" , sum ) ; } free ( x ) ; return 0 ; }