int main ( ) { int a , t , i , b , max , j , gcd , k = 0 , lcm ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; ++ i ) { k = 0 ; max = 0 ; scanf ( "%d %d" , & a , & b ) ; max = ( a > b ) ? a : b ; for ( j = 0 ; j < max ; ++ j ) { if ( a % j == 0 && b % j == 0 ) { gcd = j ; } } for ( j = 1 ; k != 1 ; ++ k , ++ j ) { if ( ( a * j ) == ( b * j ) ) { lcm = a * j ; break ; } } printf ( "\n%d %d" , gcd , lcm ) ; } return 0 ; }