main ( ) { int n , m , i , j , num , sum ; scanf ( "%d" , & n ) ; while ( n -- ) { scanf ( "%d" , & m ) ; int a [ m ] [ m ] ; for ( i = 0 ; i < m ; i ++ ) { for ( j = 0 ; j <= i ; j ++ ) scanf ( "%d" , & a [ i ] [ j ] ) ; } num = 0 ; sum = 0 ; for ( i = 0 ; i < m ; i ++ ) { sum = sum + a [ i ] [ 0 ] ; num = num + a [ i ] [ i ] ; } if ( num > sum ) printf ( "%d\n" , num ) ; else printf ( "%d\n" , sum ) ; } }