void main ( ) { int t_cases , count ; long int n ; scanf ( "%d" , & t_cases ) ; int f [ t_cases ] , c = 0 ; while ( c != t_cases ) { scanf ( "%ld" , & n ) ; long long int a [ n ] ; count = n ; for ( int i = 0 ; i < n ; i ++ ) scanf ( "%lld" , & a [ i ] ) ; for ( int i = 0 ; i < n ; i ++ ) { for ( int j = i ; j < n - 1 ; j ++ ) { if ( a [ j ] < a [ j + 1 ] ) { count ++ ; } else break ; } } f [ c ] = count ; c ++ ; } for ( int i = 0 ; i < t_cases ; i ++ ) printf ( "%d\n" , f [ i ] ) ; }