int main ( void ) { int t , a , b , max , ta , tb ; long int gcd , hcf ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d %d" , & a , & b ) ; if ( a % b == 0 || b % a == 0 ) { if ( a > b ) gcd = a ; else gcd = b ; } else if ( a == 1 || b == 1 ) { if ( a == 1 ) gcd = b ; else gcd = a ; } else { ta = a ; tb = b ; max = 2 ; gcd = 1 ; while ( ta != 1 || tb != 1 ) { if ( ta % max == 0 && tb % max != 0 ) { while ( ta % max == 0 ) { gcd = gcd * max ; ta = ta / max ; } } else if ( tb % max == 0 && ta % max != 0 ) { while ( tb % max == 0 ) { gcd = gcd * max ; tb = tb / max ; } } else if ( ta % max == 0 && tb % max == 0 ) { while ( ta % max == 0 && tb % max == 0 ) { ta = ta / max ; tb = tb / max ; gcd = gcd * max ; } } if ( ta % max > 0 && tb % max > 0 ) max ++ ; } } hcf = ( a * b ) / gcd ; printf ( "%d %d\n" , hcf , gcd ) ; } return 0 ; }